		'    FxaaFloat4 fxaaConsole360ConstDir',
		') {',
		'/*--------------------------------------------------------------------------*/',
		'    FxaaFloat2 posM;',
		'    posM.x = pos.x;',
		'    posM.y = pos.y;',
		'    #if (FXAA_GATHER4_ALPHA == 1)',
