		'        #define lumaN luma4B.z',
		'        #define lumaW luma4B.x',
		'    #else',
		'        FxaaFloat4 rgbyM = FxaaTexTop(tex, posM);',
		'        #if (FXAA_GREEN_AS_LUMA == 0)',
		'            #define lumaM rgbyM.w',
